import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./ProductList.css";

function ProductList(props) {
  let [productsList,setProducts]=useState([])

  useEffect(()=>{
    axios.get('https://fakestoreapi.com/products').then(res=>{
      setProducts(res.data)
      }).catch(error=>{
          console.log("error",error)
      })
  })

    // intial state for cart 
    var item=0;
    const [cartItem,SetItem]=useState(0)
    //reducer
   const cartReducer=(state=cartItem,action)=>{ 
        switch(action.type){
            case "AddToCart":
                return state > 0 ? state+1 : 0 
            case "RemoveFromCart":
                    return state > 0 ? state-1 : 0 
        }
    }

    // store
    var store=legacy_createStore(cartReducer)

    function addToCart(){
        store.dispatch({type:"AddToCart"})
        console.log( store.getState())
        SetItem(store.getState())
    }

    return (
        <div class="flex-container md-3">
            {
                productsList.map(product =>{

                    return   <div class="card md-3">
                                <img src={product.image} class="card-img-top" alt="Product Image" width={'200px'} height={'200px'}/>
                                <div class="card-body">
                                    <h5 class="card-title">{product.title}</h5>
                                    <p class="card-text">{product.description}</p>
                                    <div class="align-items-center">
                                        <button class="btn btn-primary" onClick={addToCart}>Add to Cart</button>
                                    </div>
                                </div>
                            </div>           

                })
            }

        </div>
    );
}

export default ProductList;
